import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function AddStudent() {
  const [student, setStudent] = useState({
    studentId: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    department: '',
    enrollmentYear: '',
    isActive: true
  });
  const [errors, setErrors] = useState({});
  const [submitStatus, setSubmitStatus] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const validate = () => {
    const newErrors = {};
    if (!student.rollNumber) newErrors.rollNumber = 'Roll number is required';
    else if (!/^\d{12}$/.test(student.rollNumber)) newErrors.rollNumber = 'Roll number must be exactly 12 digits';

    if (!student.studentId) newErrors.studentId = 'Student ID is required';
    else if (!/^[a-zA-Z0-9]+$/.test(student.studentId)) newErrors.studentId = 'Student ID must be alphanumeric';

    if (!student.firstName) newErrors.firstName = 'First name is required';
    else if (student.firstName.length < 2) newErrors.firstName = 'First name must be at least 2 characters';

    if (!student.lastName) newErrors.lastName = 'Last name is required';
    else if (student.lastName.length < 2) newErrors.lastName = 'Last name must be at least 2 characters';

    if (!student.email) newErrors.email = 'Email is required';
    else if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(student.email)) newErrors.email = 'Email must be a valid format';

    if (!student.dob) newErrors.dob = 'Date of birth is required';

    if (!student.department) newErrors.department = 'Department is required';

    if (!student.enrollmentYear) newErrors.enrollmentYear = 'Enrollment year is required';
    else if (isNaN(student.enrollmentYear) || student.enrollmentYear < 2000 || student.enrollmentYear > new Date().getFullYear()) newErrors.enrollmentYear = `Enrollment year must be between 2000 and ${new Date().getFullYear()}`;

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
    setSubmitStatus('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    setIsSubmitting(true);
    setSubmitStatus('');
    try {
      await axios.post('http://localhost:5000/students', {
        studentId: student.studentId,
        firstName: student.firstName,
        lastName: student.lastName,
        email: student.email,
        dob: student.dob,
        department: student.department,
        enrollmentYear: Number(student.enrollmentYear),
        isActive: student.isActive
      });
      setSubmitStatus('Student added successfully!');
      setStudent({
        studentId: '',
        firstName: '',
        lastName: '',
        email: '',
        dob: '',
        department: '',
        enrollmentYear: '',
        isActive: true
      });
      setTimeout(() => {
        navigate('/students');
      }, 1500);
    } catch (error) {
      setSubmitStatus('Error adding student');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit} noValidate>
        <div>
          <label>Student ID: </label>
          <input name="studentId" value={student.studentId} onChange={handleChange} />
          {errors.studentId && <div style={{ color: 'red' }}>{errors.studentId}</div>}
        </div>
        <div>
          <label>First Name: </label>
          <input name="firstName" value={student.firstName} onChange={handleChange} />
          {errors.firstName && <div style={{ color: 'red' }}>{errors.firstName}</div>}
        </div>
        <div>
          <label>Last Name: </label>
          <input name="lastName" value={student.lastName} onChange={handleChange} />
          {errors.lastName && <div style={{ color: 'red' }}>{errors.lastName}</div>}
        </div>
        <div>
          <label>Email: </label>
          <input name="email" type="email" value={student.email} onChange={handleChange} />
          {errors.email && <div style={{ color: 'red' }}>{errors.email}</div>}
        </div>
        <div>
          <label>Date of Birth: </label>
          <input name="dob" type="date" value={student.dob} onChange={handleChange} />
          {errors.dob && <div style={{ color: 'red' }}>{errors.dob}</div>}
        </div>
        <div>
          <label>Department: </label>
          <input name="department" value={student.department} onChange={handleChange} />
          {errors.department && <div style={{ color: 'red' }}>{errors.department}</div>}
        </div>
        <div>
          <label>Enrollment Year: </label>
          <input name="enrollmentYear" type="number" value={student.enrollmentYear} onChange={handleChange} />
          {errors.enrollmentYear && <div style={{ color: 'red' }}>{errors.enrollmentYear}</div>}
        </div>
        <div>
          <label>Active: </label>
          <input name="isActive" type="checkbox" checked={student.isActive} onChange={(e) => setStudent({ ...student, isActive: e.target.checked })} />
        </div>
        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Adding...' : 'Add Student'}
        </button>
      </form>
      {submitStatus && <p>{submitStatus}</p>}
    </div>
  );
}

export default AddStudent;
