import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function AddStudent() {
  const [student, setStudent] = useState({
    rollNumber: '',
    name: '',
    age: '',
    email: '',
    course: ''
  });
  const [errors, setErrors] = useState({});
  const [submitStatus, setSubmitStatus] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const validate = () => {
    const newErrors = {};
    if (!student.rollNumber) newErrors.rollNumber = 'Roll number is required';
    else if (!/^\d{12}$/.test(student.rollNumber)) newErrors.rollNumber = 'Roll number must be exactly 12 digits';

    if (!student.name) newErrors.name = 'Name is required';

    if (!student.age) newErrors.age = 'Age is required';
    else if (isNaN(student.age) || student.age <= 0) newErrors.age = 'Age must be a positive number';

    if (!student.email) newErrors.email = 'Email is required';
    else if (!student.email.endsWith('@cbit.org.in')) newErrors.email = 'Email must be a @cbit.org.in address';

    if (!student.course) newErrors.course = 'Course is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: '' });
    setSubmitStatus('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    setIsSubmitting(true);
    setSubmitStatus('');
    try {
      await axios.post('http://localhost:5000/students', {
        rollNumber: student.rollNumber,
        name: student.name,
        age: Number(student.age),
        email: student.email,
        course: student.course
      });
      setSubmitStatus('Student added successfully!');
      setStudent({
        rollNumber: '',
        name: '',
        age: '',
        email: '',
        course: ''
      });
      setTimeout(() => {
        navigate('/students');
      }, 1500);
    } catch (error) {
      setSubmitStatus('Error adding student');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit} noValidate>
        <div>
          <label>Roll Number: </label>
          <input name="rollNumber" value={student.rollNumber} onChange={handleChange} />
          {errors.rollNumber && <div style={{ color: 'red' }}>{errors.rollNumber}</div>}
        </div>
        <div>
          <label>Name: </label>
          <input name="name" value={student.name} onChange={handleChange} />
          {errors.name && <div style={{ color: 'red' }}>{errors.name}</div>}
        </div>
        <div>
          <label>Age: </label>
          <input name="age" type="number" value={student.age} onChange={handleChange} />
          {errors.age && <div style={{ color: 'red' }}>{errors.age}</div>}
        </div>
        <div>
          <label>Email: </label>
          <input name="email" type="email" value={student.email} onChange={handleChange} />
          {errors.email && <div style={{ color: 'red' }}>{errors.email}</div>}
        </div>
        <div>
          <label>Course: </label>
          <select name="course" value={student.course} onChange={handleChange}>
            <option value="">Select a course</option>
            <option value="Computer Science">Computer Science</option>
            <option value="Mechanical">Mechanical</option>
            <option value="Civil">Civil</option>
            <option value="Information Technology">Information Technology</option>
            <option value="Electronics and Communications">Electronics and Communications</option>
          </select>
          {errors.course && <div style={{ color: 'red' }}>{errors.course}</div>}
        </div>
        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Adding...' : 'Add Student'}
        </button>
      </form>
      {submitStatus && <p>{submitStatus}</p>}
    </div>
  );
}

export default AddStudent;
